mapper.insert:
INSERT INTO SURVEY(SURVEY_CODE, SURVEY_TITLE, SURVEY_CONTENTS, USER_ID, START_DATETIME, END_DATETIME, IS_OPEN, END_ALARM)
VALUES (
	  #{surveyCode}
	, #{surveyTitle}
	, #{surveyContents}
	, #{userId}
	, STR_TO_DATE(#{startDatetime}, '%Y-%m-%d %H:%i:%s')


			, STR_TO_DATE(#{endDatetime}, '%Y-%m-%d %H:%i:%s')


			, null


	, #{isOpen}
	,#{endAlarm}
)
ON DUPLICATE KEY UPDATE
	  SURVEY_TITLE=#{surveyTitle}
	, SURVEY_CONTENTS=#{surveyContents}
	, START_DATETIME=STR_TO_DATE(#{startDatetime}, '%Y-%m-%d %H:%i:%s')
	, END_DATETIME=STR_TO_DATE(#{endDatetime}, '%Y-%m-%d %H:%i:%s')
	, IS_OPEN=#{isOpen}

INSERT INTO SURVEY_MEMBER(USER_ID, SURVEY_CODE) 
VALUES 

(
	  #{item.key}
	, #{surveyCode}
)

ON DUPLICATE KEY UPDATE
	USER_ID=VALUES(USER_ID)

INSERT INTO SURVEY_QUESTION (
	  QUESTION_CODE
	, QUESTION_CONTENTS
	, IS_MULTI
	, IS_ANONYMOUS
	, SURVEY_CODE
	, SORT
) VALUES

(
	  #{item.questionCode}
	, #{item.questionContents}
	, #{item.isMulti}
	, #{item.isAnonymous}
	, #{item.surveyCode}
	, (#{index} + 1)
)

INSERT INTO SURVEY_ITEM (
	  ITEM_CODE
	, ITEM_CONTENTS
	, ITEM_TYPE
	, QUESTION_CODE
	, SURVEY_CODE
	, SORT
) VALUES

(
	  #{item.itemCode}
	, #{item.itemContents}
	, #{item.itemType}
	, #{item.questionCode}
	, #{surveyCode}
	, #{item.num}
)

sql.trim:
(
	A.USER_ID=#{userId}
	OR SURVEY_CODE IN (
		SELECT 
				SURVEY_CODE
		FROM SURVEY_MEMBER 
		WHERE
				USER_ID=#{userId}
	)		
)

	AND SURVEY_TITLE LIKE #{pattern}

mapper.update:
UPDATE SURVEY
SET
		  SURVEY_TITLE=#{surveyTitle}
		, SURVEY_CONTENTS=#{surveyContents}
		, END_DATETIME=STR_TO_DATE(#{endDatetime}, '%Y-%m-%d %H:%i:%s')
		, IS_OPEN=#{isOpen}
WHERE
		SURVEY_CODE=#{surveyCode}

mapper.insert:
INSERT INTO SURVEY_RESULT (
	  ITEM_CODE
	, USER_ID
	, QUESTION_CODE
	, SURVEY_CODE
) VALUES 

(
	  #{item.itemCode}
	, #{userId}
	, #{item.questionCode}
	, #{surveyCode}
)

INSERT INTO SURVEY_RESULT_DESC (
	  ITEM_CODE
	, USER_ID
	, ITEM_DESC
	, QUESTION_CODE
	, SURVEY_CODE
) VALUES 

(
	  #{item.itemCode}
	, #{userId}
	, #{item.desc}
	, #{item.questionCode}
	, #{surveyCode}
)

mapper.update:
UPDATE SURVEY_MEMBER
SET
		IS_COMPLETE='Y'
WHERE
		USER_ID=#{userId}
		AND SURVEY_CODE=#{surveyCode}
